import React, { useEffect, useState } from "react";

function ProfilePage() {
  const [user, setUser] = useState({username: "", id: null});
  const [editMode, setEditMode] = useState(false);
  const [form, setForm] = useState({username: "", password: ""});
  const [loading, setLoading] = useState(false);
  const [msg, setMsg] = useState("");

  // Fetch current user on mount
  useEffect(() => {
    const token = localStorage.getItem("token");
    fetch("http://localhost:8000/me", {
      headers: { "Authorization": `Bearer ${token}` }
    })
    .then(res => res.json())
    .then(data => setUser(data));
  }, []);

  const handleEdit = () => {
    setEditMode(true);
    setForm({username: user.username, password: ""});
    setMsg("");
  };

  const handleSave = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMsg("");
    const token = localStorage.getItem("token");
    try {
      const resp = await fetch("http://localhost:8000/me", {
        method: "PUT",
        headers: { 
          "Content-Type": "application/json",
          "Authorization": `Bearer ${token}` 
        },
        body: JSON.stringify(form),
      });
      const data = await resp.json();
      if (!resp.ok) {
        setMsg(data.detail || "Update failed");
        setLoading(false);
        return;
      }
      setUser(data);
      setEditMode(false);
      setMsg("Profile updated! Please log in again.");
      // Force logout and reload to login page
      setTimeout(() => {
        localStorage.removeItem("token");
        window.location.href = "/";
      }, 1800); // 1.8 seconds for user to see the message
    } catch {
      setMsg("Network error.");
    }
    setLoading(false);
  };

  return (
    <div style={{
      minWidth: 340,
      maxWidth: 420,
      background: "#fff",
      borderRadius: 18,
      padding: "32px 34px",
      boxShadow: "0 4px 20px rgba(107, 70, 193, 0.10)",
      margin: "auto"
    }}>
      <h2 style={{color:"#6b46c1", marginBottom:10}}>Profile</h2>
      <hr style={{marginBottom:24}} />
      {!editMode ? (
        <>
          <div style={{fontSize:"1.18rem", marginBottom:18}}><strong>Username:</strong> {user.username}</div>
          <div style={{fontSize:"1.18rem", marginBottom:8}}>
            <strong>Password:</strong> ********
          </div>
          <div style={{color:"#888", fontSize:"1.04rem", marginBottom:26}}>Account settings and info coming soon!</div>
          <button
            style={{
              background:"#6b46c1", color:"#fff", border:"none", borderRadius:7,
              padding:"8px 22px", fontWeight:600, fontSize:"1rem", cursor:"pointer"
            }}
            onClick={handleEdit}
          >
            Edit Profile
          </button>
        </>
      ) : (
        <form onSubmit={handleSave}>
          <div style={{marginBottom:12}}>
            <label>New Username:</label>
            <input
              type="text"
              value={form.username}
              onChange={e => setForm(f => ({...f, username: e.target.value}))}
              style={{width:"90%", padding:8, borderRadius:6, marginTop:4}}
            />
          </div>
          <div style={{marginBottom:22}}>
            <label>New Password:</label>
            <input
              type="password"
              value={form.password}
              onChange={e => setForm(f => ({...f, password: e.target.value}))}
              placeholder="Leave empty to keep current"
              style={{width:"90%", padding:8, borderRadius:6, marginTop:4}}
            />
          </div>
          <button
            type="submit"
            style={{
              background:"#6b46c1", color:"#fff", border:"none", borderRadius:7,
              padding:"8px 22px", fontWeight:600, fontSize:"1rem", cursor:"pointer", marginRight: 8
            }}
            disabled={loading}
          >{loading ? "Saving..." : "Save"}</button>
          <button
            type="button"
            style={{
              background:"#eee", color:"#6b46c1", border:"none", borderRadius:7,
              padding:"8px 22px", fontWeight:600, fontSize:"1rem", cursor:"pointer"
            }}
            onClick={() => setEditMode(false)}
          >Cancel</button>
        </form>
      )}
      {msg && <div style={{marginTop:18, color:"#6b46c1"}}>{msg}</div>}
    </div>
  );
}

export default ProfilePage;
