import React from "react";

const games = [
  { name: "Tic-Tac-Toe", icon: "‚ùå", comingSoon: false },
  { name: "Guess Number", icon: "üé≤", comingSoon: false },
  { name: "Math Quiz", icon: "üß†", comingSoon: false },
  { name: "Hangman", icon: "ü™¢", comingSoon: true }
];

function GamesPage() {
  return (
    <div style={{
      display: "grid",
      gridTemplateColumns: "1fr 1fr",
      gap: "32px",
      minWidth: 400,
      maxWidth: 600,
      margin: "auto"
    }}>
      {games.map((g, i) => (
        <div key={i} style={{
          background: "#fff",
          borderRadius: 16,
          padding: "38px 16px",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          boxShadow: "0 2px 12px rgba(107, 70, 193, 0.09)"
        }}>
          <span style={{fontSize: "2.9rem", marginBottom: 12}}>{g.icon}</span>
          <span style={{fontSize: "1.25rem", fontWeight: 600, marginBottom:8}}>{g.name}</span>
          <button disabled={g.comingSoon}
            style={{
              padding: "7px 18px",
              borderRadius: 7,
              border: "none",
              background: g.comingSoon ? "#ccc" : "#6b46c1",
              color: "#fff",
              fontWeight: 600,
              fontSize: "1rem",
              cursor: g.comingSoon ? "not-allowed" : "pointer"
            }}>
            {g.comingSoon ? "Coming Soon" : "Play"}
          </button>
        </div>
      ))}
    </div>
  );
}

export default GamesPage;
