import React from "react";
import { useNavigate } from "react-router-dom";
import "./GamePage.css";

const games = [
  { name: "Tic-Tac-Toe", icon: "âŒ", comingSoon: false },
  { name: "Guess Number", icon: "ðŸŽ²", comingSoon: false },
  { name: "Math Quiz", icon: "ðŸ§ ", comingSoon: false },
  { name: "Hangman", icon: "ðŸª¢", comingSoon: true }
];

function GamesPage() {
  const navigate = useNavigate();

  const handlePlay = (gameName) => {
    if (gameName === "Tic-Tac-Toe") navigate("/games/tictactoe");
    // Add similar lines for other games as you implement them
  };

  return (
    <div className="games-grid">
      {games.map((g, i) => (
        <div className="game-card" key={i}>
          <span className="game-card-icon">{g.icon}</span>
          <span className="game-card-title">{g.name}</span>
          <button
            className="game-play-btn"
            disabled={g.comingSoon}
            onClick={() => !g.comingSoon && handlePlay(g.name)}
          >
            {g.comingSoon ? "Coming Soon" : "Play"}
          </button>
        </div>
      ))}
    </div>
  );
}

export default GamesPage;
