import React, { useEffect, useState } from "react";

function ProfilePage() {
  const [user, setUser] = useState({username: "", id: null});
  const [editMode, setEditMode] = useState(false);
  const [msg, setMsg] = useState("");
  const [loading, setLoading] = useState(false);

  // Password change state
  const [pwStep, setPwStep] = useState(0); // 0: enter current pw, 1: enter new pw
  const [curPw, setCurPw] = useState("");
  const [newPw, setNewPw] = useState("");
  const [newPw2, setNewPw2] = useState("");
  const [verifiedPw, setVerifiedPw] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem("token");
    fetch("http://localhost:8000/me", {
      headers: { "Authorization": `Bearer ${token}` }
    })
    .then(res => res.json())
    .then(data => setUser(data));
  }, []);

  const handleEdit = () => {
    setEditMode(true);
    setMsg("");
    setPwStep(0);
    setCurPw("");
    setNewPw("");
    setNewPw2("");
    setVerifiedPw(false);
  };

  // Step 1: Verify current password with backend (separate endpoint or try update with dummy new pw)
  const verifyCurrentPw = async (e) => {
    e.preventDefault();
    setMsg("");
    setLoading(true);
    const token = localStorage.getItem("token");
    try {
      // We'll POST to a dedicated endpoint to check password.
      // You need to add this endpoint in your backend (see below)
      const resp = await fetch("http://localhost:8000/verify-password", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Bearer ${token}`
        },
        body: JSON.stringify({ password: curPw })
      });
      if (resp.ok) {
        setVerifiedPw(true);
        setPwStep(1);
      } else {
        setMsg("Incorrect current password.");
      }
    } catch {
      setMsg("Server error.");
    }
    setLoading(false);
  };

  // Step 2: Save new password
  const handleSavePw = async (e) => {
    e.preventDefault();
    setMsg("");
    if (!newPw || !newPw2) {
      setMsg("Please fill both fields.");
      return;
    }
    if (newPw !== newPw2) {
      setMsg("New passwords do not match.");
      return;
    }
    setLoading(true);
    const token = localStorage.getItem("token");
    try {
      const resp = await fetch("http://localhost:8000/me", {
        method: "PUT",
        headers: { 
          "Content-Type": "application/json",
          "Authorization": `Bearer ${token}` 
        },
        body: JSON.stringify({
          password: newPw,
          old_password: curPw
        }),
      });
      const data = await resp.json();
      if (!resp.ok) {
        setMsg(data.detail || "Update failed");
        setLoading(false);
        return;
      }
      setMsg("Password changed! Please log in again.");
      setTimeout(() => {
        localStorage.removeItem("token");
        window.location.href = "/";
      }, 1600);
    } catch {
      setMsg("Server error.");
    }
    setLoading(false);
  };

  return (
    <div style={{
      minWidth: 340,
      maxWidth: 420,
      background: "#fff",
      borderRadius: 18,
      padding: "32px 34px",
      boxShadow: "0 4px 20px rgba(107, 70, 193, 0.10)",
      margin: "auto"
    }}>
      <h2 style={{color:"#6b46c1", marginBottom:10}}>Profile</h2>
      <hr style={{marginBottom:24}} />
      {!editMode ? (
        <>
          <div style={{fontSize:"1.18rem", marginBottom:18}}><strong>Username:</strong> {user.username}</div>
          <div style={{fontSize:"1.18rem", marginBottom:8}}>
            <strong>Password:</strong> ********
          </div>
          <button
            style={{
              background:"#6b46c1", color:"#fff", border:"none", borderRadius:7,
              padding:"8px 22px", fontWeight:600, fontSize:"1rem", cursor:"pointer"
            }}
            onClick={handleEdit}
          >
            Edit Profile
          </button>
        </>
      ) : (
        <div>
          {pwStep === 0 && (
            <form onSubmit={verifyCurrentPw}>
              <div style={{marginBottom:14}}>
                <label>Current Password:</label>
                <input
                  type="password"
                  value={curPw}
                  onChange={e => setCurPw(e.target.value)}
                  style={{width:"90%", padding:8, borderRadius:6, marginTop:4}}
                  autoFocus
                  autoComplete="current-password"
                />
              </div>
              <button type="submit" style={{
                background:"#6b46c1", color:"#fff", border:"none", borderRadius:7,
                padding:"8px 22px", fontWeight:600, fontSize:"1rem", cursor:"pointer"
              }} disabled={loading}>
                {loading ? "Checking..." : "Next"}
              </button>
            </form>
          )}
          {pwStep === 1 && (
            <form onSubmit={handleSavePw}>
              <div style={{marginBottom:14}}>
                <label>New Password:</label>
                <input
                  type="password"
                  value={newPw}
                  onChange={e => setNewPw(e.target.value)}
                  style={{width:"90%", padding:8, borderRadius:6, marginTop:4}}
                  autoFocus
                  autoComplete="new-password"
                />
              </div>
              <div style={{marginBottom:24}}>
                <label>Repeat New Password:</label>
                <input
                  type="password"
                  value={newPw2}
                  onChange={e => setNewPw2(e.target.value)}
                  style={{width:"90%", padding:8, borderRadius:6, marginTop:4}}
                  autoComplete="new-password"
                />
              </div>
              <button type="submit" style={{
                background:"#6b46c1", color:"#fff", border:"none", borderRadius:7,
                padding:"8px 22px", fontWeight:600, fontSize:"1rem", cursor:"pointer"
              }} disabled={loading}>
                {loading ? "Saving..." : "Save"}
              </button>
            </form>
          )}
          {msg && <div style={{marginTop:18, color:"#6b46c1"}}>{msg}</div>}
        </div>
      )}
    </div>
  );
}

export default ProfilePage;
